import sailpoint.object.*;
import sailpoint.api.*;
  import sailpoint.object.ProvisioningPlan.AccountRequest;
  import sailpoint.integration.ProvisioningPlan.AttributeRequest;
  import sailpoint.object.ProvisioningPlan.AttributeRequest;
  import sailpoint.object.Schema;

  Map map = new HashMap();
  map.put("Domain","192.168.33.1");
  Attributes at = new Attributes();
  at.setMap(map);
  
  
if(plan != null){
  List accreqs = plan.getAccountRequests();
  for(int i=0; i<accreqs.size(); i++){
    AccountRequest acr = (AccountRequest) accreqs.get(i);
    List attreqs = acr.getAttributeRequests();
    for(int j=0; j<attreqs.size(); j++){
      AttributeRequest ar = (AttributeRequest) attreqs.get(j);
      String str = ar.getName();
      System.out.println("==============="+str);
      List schemas = application.getSchemas();
      for(Schema sch:schemas){
        System.out.println("---------------++++++"+sch);
        List attrDef = sch.getAttributes();
        for(AttributeDefinition adef : attrDef){
          System.out.println("***********************-"+adef.getName());
          if(adef.getName().equals(str))
          	ar.setArguments(at);
          
        }
        	
      }
     
    }
  }
  System.out.println("+++++++++++++++++++beforplan-"+plan.toXml());
}
